        var elem = document.getElementById("progressBar");
        var text = document.getElementById("progressText") 
        var width = 0;
        var id = setInterval(frame, 20);
        function frame() {
          if (width >= 33) {
            clearInterval(id);
          } else {
            width++; 
            elem.style.width = width + '%';
            text.style.marginLeft = width + '%';
            text.innerHTML = width * 1  + '%';
